        public override void KeyDown(NSEvent theEvent)
        {
            if (IsListening)
            {
                // check if we are in the count off
                if (Metronome.Instance.Mixer.CountOffSampleDuration > 0)
                {
                    Taps.AddLast(0);
                }
                else
                {
					Taps.AddLast(Metronome.Instance.ElapsedBpm);
                }
            }
